const hitsArray = hitData.value.hitsArray;

// Define axis labels
const xLabels = ['Far Left', 'Left', 'Center', 'Right', 'Far Right'];
const yLabels = ['Back', 'Mid-Back', 'Center', 'Mid-Front', 'Front'];

// Initialize z matrix (5x5 grid)
const z = Array(yLabels.length)
  .fill(0)
  .map(() => Array(xLabels.length).fill(0));

// Process hitsArray to populate z matrix
hitsArray.forEach(hit => {
  const x = hit.XIndex;
  const y = hit.YIndex;

  // Ensure indices are within bounds
  if (
    x >= 0 &&
    x < xLabels.length &&
    y >= 0 &&
    y < yLabels.length
  ) {
    z[y][x] += 1;
  }
});

// Generate annotations for the heatmap
const annotations = [];

for (let y = 0; y < yLabels.length; y++) {
  for (let x = 0; x < xLabels.length; x++) {
    annotations.push({
      x: xLabels[x],
      y: yLabels[y],
      text: z[y][x].toString(),
      showarrow: false,
      font: { color: 'black', size: 14 },
      xref: 'x1',
      yref: 'y1',
    });
  }
}

// Define the background image URL
const backgroundImageURL = 'https://drive.google.com/file/d/1N7d9UvK5WB96D7Rlcs7LoJTOWkYv5BjU/view?usp=drive_link'; // Replace with your actual image URL

// Prepare data and layout for the heatmap
const data = [
  {
    type: 'heatmap',
    z: z,
    x: xLabels,
    y: yLabels,
    colorscale: 'YlOrRd',
    reversescale: true,
    showscale: false,
    hoverinfo: 'skip',
  },
];

const layout = {
  xaxis: {
    showticklabels: false,
    showgrid: false,
    zeroline: false,
    ticks: '',
    showline: false,
  },
  yaxis: {
    showticklabels: false,
    showgrid: false,
    zeroline: false,
    autorange: 'reversed',
    ticks: '',
    showline: false,
  },
  annotations: annotations,
  images: [
    {
      source: backgroundImageURL,
      xref: 'paper',
      yref: 'paper',
      x: 0,
      y: 1,
      sizex: 1,
      sizey: 1,
      sizing: 'stretch',
      opacity: 0.5,
      layer: 'below',
    },
  ],
  margin: {
    l: 0,
    r: 0,
    t: 0,
    b: 0,
  },
};

return {
  data: data,
  layout: layout,
};
