  const x = hit.XIndex;
  const y = hit.YIndex;
  if (x >= 0 && x < xLabels.length && y >= 0 && y < yLabels.length) {
    z[y][x] += 1;
  }
});

// Compute zmin and zmax based on positive values only
const zValues = z.flat().filter((value) => value > 0);
const zmin = zValues.length > 0 ? Math.min(...zValues) : 0;
const zmax = zValues.length > 0 ? Math.max(...zValues) : 1;

// Replace zero values with null to make them transparent
const zHeatmap = z.map((row) => row.map((value) => (value === 0 ? null : value)));

// Generate annotations for non-zero cells only
const annotations = [];
for (let y = 0; y < yLabels.length; y++) {
  for (let x = 0; x < xLabels.length; x++) {
    if (z[y][x] > 0) { // Only add annotation if value is greater than 0
      annotations.push({
        x: xLabels[x],
        y: yLabels[y],
        text: z[y][x].toString(),
        showarrow: false,
        font: { color: 'grey', size: 14 },
        xref: 'x1',
        yref: 'y1',
      });
    }
  }
}

// Background image URL
const backgroundImageURL = 'https://i.imgur.com/ceMlXSf.png';

// Heatmap data with opacity set to 0.5 for semi-transparency
const data = [
  {
    type: 'heatmap',
    z: zHeatmap,
    x: xLabels,
    y: yLabels,
    colorscale: 'YlOrRd', // Use any colorscale you prefer
    reversescale: true,
    showscale: false,
    hoverinfo: 'skip',
    zmin: zmin,
    zmax: zmax,
    opacity: 0.5, // Semi-transparent non-zero cells
  },
];

// Layout settings
const layout = {
  xaxis: {
    showticklabels: false,
    showgrid: false,
    zeroline: false,
    ticks: '',
    showline: false,
  },
  yaxis: {
    showticklabels: false,
    showgrid: false,
    zeroline: false,
    autorange: 'reversed',
    ticks: '',
    showline: false,
  },
  annotations: annotations,
  images: [
    {
      source: backgroundImageURL,
      xref: 'paper',
      yref: 'paper',
      x: 0,
      y: 1,
      sizex: 1,
      sizey: 1,
      sizing: 'stretch',
      opacity: 0.5, // Adjust as needed
      layer: 'below',
    },
  ],
  margin: {
    l: 0,
    r: 0,
    t: 0,
    b: 0,
  },
};

// Set the chart data with the filtered data and layout
chartData.setValue({ data, layout });
