"]],"^4C",null,"^4D",null,"^4E",null,"^4F",null,"^4G",null,"^4H","","^7","~m1728483328571","^4I","~m1743168339448","^4J","RivalAnalysis","^4K",null,"^4L",null,"^4M",null,"^4N",null]]],"testing",["^0",["^ ","n","pluginTemplate","v",["^ ","id","testing","^4:",null,"^4;",null,"^4<","datasource","^4=","JavascriptQuery","^4>",null,"^4?","JavascriptQuery","^4@",null,"^4A",["^4B",["queryRefreshTime","","allowedGroupIds",["^B",[]],"streamResponse",false,"lastReceivedFromResourceAt",null,"isFunction",false,"functionParameters",null,"queryDisabledMessage","","servedFromCache",false,"offlineUserQueryInputs","","functionDescription",null,"successMessage","","queryDisabled","","playgroundQuerySaveId","latest","workflowParams",null,"resourceNameOverride","","runWhenModelUpdates",false,"workflowRunExecutionType","sync","showFailureToaster",true,"query","// Initial frontCourt object
const frontCourt = {
  setter: { inFront: false, playerID: "S1" },
  opposite: { inFront: false, playerID: "O1" },
  OH1: { inFront: false, playerID: "OH1" },
  OH2: { inFront: false, playerID: "OH2" },
  MB1: { inFront: false, playerID: "MB1" },
  MB2: { inFront: false, playerID: "MB2" }
};

// Toggle function
function togglePlayer(position) {
  // Toggle the player's 'inFront' status
  frontCourt[position].inFront = !frontCourt[position].inFront;

  // Run validation after each change
  const validation = validateFrontCourt(frontCourt);

  if (validation === true) {
    updateButtonBackgrounds(frontCourt); // Valid case, update UI
  } else {
    alert(validation); // Show validation error
    frontCourt[position].inFront = false; // Reset toggle if validation fails
  }
}

// Validation function
function validateFrontCourt(frontCourt) {
  const numOHInFront = (frontCourt.OH1.inFront ? 1 : 0) + (frontCourt.OH2.inFront ? 1 : 0);
  const numMBInFront = (frontCourt.MB1.inFront ? 1 : 0) + (frontCourt.MB2.inFront ? 1 : 0);
  const numSetterOrOppositeInFront = (frontCourt.setter.inFront ? 1 : 0) + (frontCourt.opposite.inFront ? 1 : 0);

  // Validation checks
  if (numOHInFront > 1) {
    return "You can't have both OH1 and OH2 in the front court!";
  }
  if (numMBInFront > 1) {
    return "You can't have both MB1 and MB2 in the front court!";
  }
  if (numSetterOrOppositeInFront > 1) {
    return "You can't have both the Setter and the Opposite in the front court!";
  }

  return true; // Everything is valid
}

// Update button backgrounds based on the 'inFront' status
function updateButtonBackgrounds(frontCourt) {
  document.getElementById('buttonSetter').style.backgroundColor = frontCourt.setter.inFront ? "blue" : "defaultColor";
  document.getElementById('buttonOpposite').style.backgroundColor = frontCourt.opposite.inFront ? "blue" : "defaultColor";
  document.getElementById('buttonOH1').style.backgroundColor = frontCourt.OH1.inFront ? "blue" : "defaultColor";
  document.getElementById('buttonOH2').style.backgroundColor = frontCourt.OH2.inFront ? "blue" : "defaultColor";
  document.getElementById('buttonMB1').style.backgroundColor = frontCourt.MB1.inFront ? "blue" : "defaultColor";
  document.getElementById('buttonMB2').style.backgroundColor = frontCourt.MB2.inFront ? "blue" : "defaultColor";
}

// Example button click event listeners
document.getElementById('setter').addEventListener('click', function() {
  togglePlayer("setter");
});

document.getElementById('buttonOpposite').addEventListener('click', function() {
  togglePlayer("opposite");
});

document.getElementById('buttonOH1').addEventListener('click', function() {
  togglePlayer("OH1");
});

document.getElementById('buttonOH2').addEventListener('click', function() {
  togglePlayer("OH2");
});

document.getElementById('buttonMB1').addEventListener('click', function() {
  togglePlayer("MB1");
});

document.getElementById('buttonMB2').addEventListener('click', function() {
  togglePlayer("MB2");
});
