  let trend = scoreData.value.map((item) => item.yourScore - item.opponentScore); // Trend values
  let actionNos = scoreData.value.map((_, index) => index + 1); // Action Numbers

  // Separate arrays for upward and downward trends
  let upward = { 
    x: [], 
    y: [], 
    mode: 'lines+markers', 
    line: { color: 'green' }, 
    showlegend: false // Disable legend for this trace
  };
  let downward = { 
    x: [], 
    y: [], 
    mode: 'lines+markers', 
    line: { color: 'red' }, 
    showlegend: false // Disable legend for this trace
  };

  for (let i = 0; i < trend.length - 1; i++) {
    if (trend[i + 1] > trend[i]) {
      // Add both current and next points to the upward trend
      upward.x.push(actionNos[i], actionNos[i + 1]);
      upward.y.push(trend[i], trend[i + 1]);
      // Close the downward trace to avoid overlaps
      if (downward.x.length > 0) {
        downward.x.push(null); // Disconnect the downward line
        downward.y.push(null);
      }
    } else {
      // Add both current and next points to the downward trend
      downward.x.push(actionNos[i], actionNos[i + 1]);
      downward.y.push(trend[i], trend[i + 1]);
      // Close the upward trace to avoid overlaps
      if (upward.x.length > 0) {
        upward.x.push(null); // Disconnect the upward line
        upward.y.push(null);
      }
    }
  }

  return [upward, downward]; // Return the two traces
})()
