          (stat.byPlayer[player].locationTempo[cleanLocation] || 0) + 1;

        stat.locationTempoCounts[cleanLocation] =
          (stat.locationTempoCounts[cleanLocation] || 0) + 1;
      }
    } else {
      stat.byPlayer[player]++;
    }
  };

  rawData.forEach(point => {
    const {
      winning_team,
      action_type,
      action,
      locationTempo,
      home_player,
      opponent_player
    } = point;

    const actionType = action_type?.trim();
    const actionLabel = action?.toLowerCase().trim();

    // --- Opponent Errors (flipped logic: awarded to team who got the point) ---
    const errorTypes = ["sp. e.", "ser. e.", "op. e.", "other"];
    if (errorTypes.includes(actionType?.toLowerCase())) {
      const awardedTeam = winning_team;
      addToStat("Opponent Errors", awardedTeam, "Unknown", locationTempo);
    }

    // --- Aces (robust match) ---
    if (
      (actionType === "Op. Ace" || (actionType === "Ser." && actionLabel?.includes("ace"))) &&
      winning_team
    ) {
      const acePlayer = winning_team === "home" ? home_player : opponent_player;
      addToStat("Aces", winning_team, acePlayer, locationTempo);
    }

    // --- Attacks (robust match) ---
    const attackTypes = ["att.", "op. att."];
    const validAttacks = ["hard spike", "tip/roll", "setter dump", "touch out"];
    if (
      attackTypes.includes(actionType?.toLowerCase()) &&
      validAttacks.includes(actionLabel)
    ) {
      const attackerTeam = winning_team;
      const attackerPlayer =
        attackerTeam === "home" ? home_player : opponent_player;
      addToStat("Attacks", attackerTeam, attackerPlayer, locationTempo);
    }
  });

  return StatsData;
})()}}
