  const selectedSet = segmentedControl5.value;
  const sets = PointsData.value || [];

  // Clean nulls
  const cleaned = sets.filter(p => p && p.points);

  // Use correct set or merge all
  const points = selectedSet === "Total"
    ? cleaned.flatMap(p => p.points)
    : (cleaned.find(p => String(p.set_number) === selectedSet)?.points || []);

  const buildChartData = (statType, label, playerKey) => {
    const counts = {};

    points.forEach(p => {
      const player = p[playerKey]?.trim();
      if (p.winning_team === "opponent" && player) {
        if (
          (statType === "Attack" && p.action_type === "Op. Att.") ||
          (statType === "Ace" && p.action_type === "Op. Ace")
        ) {
          counts[player] = (counts[player] || 0) + 1;
        }
      }
    });

    const total = Object.values(counts).reduce((a, b) => a + b, 0);

    if (total === 0) {
      return [{
        type: "bar",
        name: "None",
        x: [100],
        y: [label],
        orientation: "h",
        text: ["0"],
        textposition: "auto",
        marker: { color: "#e0e0e0" },
        hoverinfo: "text",
        hovertext: `No ${label.toLowerCase()} recorded`
      }];
    }

    return Object.entries(counts)
      .map(([player, count]) => ({
        player,
        count,
        percent: (count / total) * 100
      }))
      .sort((a, b) => b.percent - a.percent)
      .map(item => ({
        type: "bar",
        name: item.player,
        x: [item.percent.toFixed(1)],
        y: [label],
        orientation: "h",
        text: [`${item.count}`],
        textposition: "auto",
        hovertemplate: `${item.player}<br>${label}: ${item.count}<br>Ratio: ${item.percent.toFixed(1)}%<extra></extra>`
      }));
  };

  const attackBars = buildChartData("Attack", "Hits", "opponent_player");
  const aceBars = buildChartData("Ace", "Aces", "opponent_player");

  return [...attackBars, ...aceBars];
})()}}
